import React, { useState, useMemo } from "react";
import axios from "axios";

// LISTING TASKS
function ListTodo() {
  const [data, setData] = useState([]);
  const [count, setCount] = useState(0);

  async function getData() {
    await axios
      .get("http://localhost:8080/todos")
      .then((res) => setData(res.data));
    setCount(count + 1);
  }

  useMemo(() => {
    getData();
    console.log("dsasa");
  }, [count]);

  const toggleIsCompleted = (id, completed) => {
    putData({ id, data: { completed } });
  };

  const putData = async ({ id, data }) => {
    const res = await axios.put(`http://localhost:8080/todos/task/${id}`, data);
    setCount(count + 1);
    return res.data;
  };

  return (
    <div>
      {data.map((i) => (
        <div key={i.titleId}>
          {i.todoSubEntities.map((item) => (
            <li key={item.taskId}>
              <input
                type="checkbox"
                checked={item.completed}
                onChange={() => toggleIsCompleted(item.taskId, !item.completed)}
              />
              {item.completed ? (
                <strike>{i.title + " *** " + item.task}</strike>
              ) : (
                i.title + " *** " + item.task
              )}
            </li>
          ))}
        </div>
      ))}
    </div>
  );
}

export default ListTodo;
